import React from 'react';

export default function ToggleTodo({ id, checked, setTodoList }) {
  const onToggleTodo = (event) => {
    setTodoList((prevState) =>
      prevState.map((todo) => {
        if (todo.id === id) {
          return { ...todo, done: !todo.done };
        } else {
          return todo;
        }
      })
    );
  };

  return (
    <>
      <input type='checkbox' onChange={(event) => onToggleTodo(event)} />
    </>
  );
}
